#include "common.h"

static const int _ = []() {
    std::ios_base::sync_with_stdio(0);
    std::cin.tie(0);
    return 0;
}();

#pragma GCC optimize("Ofast")
class Solution
{
public:
    struct Window
    {
        int begin;
        int end;

        inline bool is_valid(const std::vector<int> &nums)
        {
            int min = INT_MAX;
            int max = INT_MIN;
            for (int idx = begin; idx <= end; ++idx)
            {
                min = std::min(min, nums[idx]);
                max = std::max(max, nums[idx]);
            }
            return std::abs(max - min) <= 2 ? true : false;
        }
    };

    long long continuousSubarrays(std::vector<int> &nums)
    {
        long long acc = 0;
        for (int w_size = 0; w_size < nums.size(); ++w_size) // window size
        {
            for (int w_begin = 0; w_begin < nums.size() - w_size; ++w_begin)
            {
                Window w = {w_begin, w_begin + w_size};
                if (w.is_valid(nums))
                    ++acc;
            }
        }
        return acc;
    }
};

TEST(p2762, Simple)
{
    Solution s;

    std::vector<int> in{5, 4, 2, 4};
    ASSERT_EQ(s.continuousSubarrays(in), 8);

    std::vector<int> in2{1, 2, 3};
    ASSERT_EQ(s.continuousSubarrays(in2), 6);
}

TEST(p2762, Long)
{
    Solution s;
    std::vector<int> in{65,66,67,66,66,65,64,65,65,64};
    ASSERT_EQ(s.continuousSubarrays(in), 43);
}


